import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import CategoryIcons from '../components/CategoryIcons';
import ProductCard from '../components/ProductCard';
import ReviewCard from '../components/ReviewCard';
import SubscribeSection from '../components/SubscribeSection';
import FeaturedSection from '../components/FeaturedSection';

// Dummy data
import { coffeeProducts, dessertProducts, customerReviews } from '../data/dummyData';

function Home() {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />
      {/* Hero Section */}
      <section
        className="relative bg-coffeeBrown bg-cover bg-center h-[400px] flex flex-col justify-center items-center text-cream px-4"
        style={{ backgroundImage: "url('/assets/hero-coffee.jpg')" }}
        aria-label="Hero section with coffee cup and tagline"
      >
        <h1 className="text-4xl md:text-5xl font-semibold mb-4 text-center drop-shadow-lg">
          We pledged to serve purely homemade dishes to your doorstep
        </h1>
        <a
          href="/menu"
          className="bg-cream text-coffeeBrown px-6 py-3 rounded-full font-semibold hover:bg-lightBrown transition inline-block"
          aria-label="Visit Menu"
        >
          Visit Menu
        </a>
      </section>

      {/* Category Icons Section */}
      <CategoryIcons />

      {/* Our Special Coffee Section */}
      <section className="px-6 py-12 bg-cream">
        <h2 className="text-3xl font-semibold mb-8 text-center">Our Special Coffee</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {coffeeProducts.map((product) => (
            <ProductCard key={product.id} product={product} addToCart={addToCart} />
          ))}
        </div>
      </section>

      {/* Our Special Dessert Section */}
      <section className="px-6 py-12 bg-lightBrown">
        <h2 className="text-3xl font-semibold mb-8 text-center text-cream">Our Special Desserts</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {dessertProducts.map((product) => (
            <ProductCard key={product.id} product={product} addToCart={addToCart} />
          ))}
        </div>
      </section>

      {/* Featured Section */}
      <FeaturedSection />

      {/* Customer Reviews Section */}
      <section className="px-6 py-12 bg-cream">
        <h2 className="text-3xl font-semibold mb-8 text-center">Customer Reviews</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-8">
          {customerReviews.map((review) => (
            <ReviewCard key={review.id} review={review} />
          ))}
        </div>
      </section>

      {/* Subscribe Section */}
      <SubscribeSection />

      <Footer />
    </div>
  );
}

export default Home;
