import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import axios from 'axios';

function Owner() {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !price || !image) {
      setMessage('Please fill all fields.');
      return;
    }
    try {
      await axios.post('http://localhost:8080/api/items', {
        name,
        price: parseFloat(price),
        image,
      });
      setMessage('Item added successfully!');
      setName('');
      setPrice('');
      setImage('');
    } catch (error) {
      setMessage('Error adding item.');
    }
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />
      <main className="flex-grow px-6 py-12 bg-cream text-coffeeBrown max-w-md mx-auto">
        <h1 className="text-3xl font-semibold mb-6 text-center">Add New Item</h1>
        <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
          <input
            type="text"
            placeholder="Name"
            className="px-4 py-2 border border-coffeeBrown rounded"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="number"
            step="0.01"
            placeholder="Price"
            className="px-4 py-2 border border-coffeeBrown rounded"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Image URL"
            className="px-4 py-2 border border-coffeeBrown rounded"
            value={image}
            onChange={(e) => setImage(e.target.value)}
            required
          />
          <button
            type="submit"
            className="bg-coffeeBrown text-cream py-3 rounded hover:bg-lightBrown transition"
          >
            Add Item
          </button>
        </form>
        {message && <p className="mt-4 text-center">{message}</p>}
      </main>
      <Footer />
    </div>
  );
}

export default Owner;
