import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import ProductCard from '../components/ProductCard';
import { coffeeProducts, dessertProducts } from '../data/dummyData';

function Shop() {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  const allProducts = [...coffeeProducts, ...dessertProducts];

  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />
      <main className="px-6 py-12 bg-cream flex-grow">
        <h1 className="text-4xl font-semibold mb-8 text-center text-coffeeBrown">Shop</h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {allProducts.map((product) => (
            <ProductCard key={product.id} product={product} addToCart={addToCart} />
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default Shop;
