import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import axios from 'axios';

function Owner() {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [message, setMessage] = useState('');

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setPreview(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !price) {
      setMessage('Please fill all fields.');
      return;
    }
    try {
      const formData = new FormData();
      formData.append('name', name);
      formData.append('price', parseFloat(price));
      if (imageFile) {
        formData.append('image', imageFile);
      }

      await axios.post('http://localhost:8080/api/items', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMessage('Item added successfully!');
      setName('');
      setPrice('');
      setImageFile(null);
      setPreview(null);
    } catch (error) {
      setMessage('Error adding item.');
    }
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />
      <main className="flex-grow px-6 py-12 bg-cream text-coffeeBrown max-w-md mx-auto">
        <h1 className="text-3xl font-semibold mb-6 text-center">Add New Item</h1>
        <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
          <input
            type="text"
            placeholder="Name"
            className="px-4 py-2 border border-coffeeBrown rounded"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="number"
            step="0.01"
            placeholder="Price"
            className="px-4 py-2 border border-coffeeBrown rounded"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
          <input
            type="file"
            accept="image/*"
            className="px-4 py-2 border border-coffeeBrown rounded"
            onChange={handleImageChange}
          />
          {preview && (
            <img
              src={preview}
              alt="Preview"
              className="w-full h-48 object-contain rounded mt-2"
            />
          )}
          <button
            type="submit"
            className="bg-coffeeBrown text-cream py-3 rounded hover:bg-lightBrown transition"
          >
            Add Item
          </button>
        </form>
        {message && <p className="mt-4 text-center">{message}</p>}
      </main>
      <Footer />
    </div>
  );
}
