import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import CategoryIcons from '../components/CategoryIcons';
import SubscribeSection from '../components/SubscribeSection';
import FeaturedSection from '../components/FeaturedSection';
import { ItemsContext } from '../contexts/ItemsContext';

function Home() {
  const [cart, setCart] = useState([]);
  const [fetchedItems, setFetchedItems] = useState([]);
  const { items } = useContext(ItemsContext);

  useEffect(() => {
    axios.get('http://localhost:8080/api/items')
      .then(response => {
        setFetchedItems(response.data);
      })
      .catch(error => {
        console.error('Error fetching items:', error);
      });
  }, []);

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  const combinedItems = [...fetchedItems, ...items];

  console.log('Home page combinedItems:', combinedItems);
  console.log('Context items:', items);

  // For simplicity, show all items in one section
  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />
      {/* Hero Section */}
      <section
        className="relative bg-coffeeBrown bg-cover bg-center h-[400px] flex flex-col justify-center items-center text-cream px-4"
        style={{ backgroundImage: "url('/assets/hero-coffee.jpg')" }}
        aria-label="Hero section with coffee cup and tagline"
      >
        <h1 className="text-4xl md:text-5xl font-semibold mb-4 text-center drop-shadow-lg">
          We pledged to serve purely homemade dishes to your doorstep
        </h1>
        <a
          href="/menu"
          className="bg-cream text-coffeeBrown px-6 py-3 rounded-full font-semibold hover:bg-lightBrown transition inline-block"
          aria-label="Visit Menu"
        >
          Visit Menu
        </a>
      </section>

      {/* Category Icons Section */}
      <CategoryIcons />

      {/* Items Section */}
      <FeaturedSection items={combinedItems} />

      {/* Subscribe Section */}
      <SubscribeSection />

      <Footer />
    </div>
  );
}

export default Home;
